import "core-js/modules/es.array.push.js";
import { ref } from '@vue/reactivity';
import { useRouter } from 'vue-router';
import axios from 'axios';
export default {
  name: 'HomePage',
  setup() {
    const router = useRouter();
    let loginInfo = ref(false);
    let myInfo = ref(false);
    let nickName = ref('');
    let profileModal = ref(false);
    axios.get('/api/customer/login-check').then(res => {
      console.log(res.data);
      if (res.data === 'not login') {
        loginInfo.value = true;
        myInfo.value = false;
      } else {
        nickName.value = res.data;
        loginInfo.value = false;
        myInfo.value = true;
      }
    });
    function login() {
      router.push({
        path: '/customer/login'
      });
    }
    function signUp() {
      router.push({
        path: '/customer/sign-up'
      });
    }
    function logout() {
      axios.get('/api/customer/logout').then(res => {
        console.log(res.data);
        if (res.data === 'logout success') {
          alert('로그아웃 되었습니다.');
          loginInfo.value = true;
          myInfo.value = false;
          router.push({
            path: '/'
          });
        }
      }).catch(err => {
        console.log(err);
      });
    }
    function profileLogout() {
      axios.get('/api/customer/logout').then(res => {
        console.log(res.data);
        if (res.data === 'logout success') {
          alert('로그아웃 되었습니다.');
          loginInfo.value = true;
          myInfo.value = false;
          router.push({
            path: '/'
          });
          profileModal.value = false;
        }
      }).catch(err => {
        console.log(err);
      });
    }
    function loginUpdate(loginData) {
      nickName.value = loginData[0];
      loginInfo.value = loginData[1];
      myInfo.value = loginData[2];
    }
    function profileClick() {
      axios.get('/api/customer/login-check').then(res => {
        if (res.data === 'not login') {
          alert('로그인이 필요합니다.');
          router.push({
            path: '/customer/login'
          });
        } else profileModal.value = true;
      });
    }
    function profileDelete() {
      profileModal.value = false;
    }
    function mypage() {
      router.push({
        name: 'MyPage',
        query: {
          nickName: nickName
        }
      });
    }
    return {
      login,
      signUp,
      loginInfo,
      myInfo,
      nickName,
      logout,
      loginUpdate,
      profileClick,
      profileModal,
      profileDelete,
      profileLogout,
      mypage
    };
  }
};
